import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { getAllMoviesThunk } from '../../store/movies';

export default function TrailerPage() {
    const dispatch = useDispatch();
    const { movieId } = useParams();
    const movies = Object.values(useSelector(state => state.movies))
    const movie = movies[+movieId]
    console.log(movie)

    useEffect(() => {
        dispatch(getAllMoviesThunk())
    }, [dispatch])

    if (!movies) return null;

    return (
        <div>

            <h1>Trailer</h1>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" class="Hawkins-Icon Hawkins-Icon-Standard"><path fill-rule="evenodd" clip-rule="evenodd" d="M24 11.0001L3.41421 11.0001L8.70711 5.70718L7.29289 4.29297L0.292892 11.293C0.105356 11.4805 0 11.7349 0 12.0001C0 12.2653 0.105356 12.5196 0.292892 12.7072L7.29289 19.7072L8.70711 18.293L3.41421 13.0001H24V11.0001Z" fill="currentColor"></path></svg>
        </div>
    )
}
