import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import 'swiper/swiper.min.css';
import 'swiper/modules/pagination/pagination.min.css';
import 'swiper/modules/navigation/navigation.min.css';

import './styles.css';

// import required modules
import { Pagination, Navigation } from "swiper";
import { getAllMoviesThunk } from '../../store/movies';


export default function HomePageCarousel() {
    const dispatch = useDispatch();
    const movies = Object.values(useSelector(state => state.movies))
    const moviesCarousel = movies.slice(0, 18)
    const [showDetails, setShowDetails] = useState(false);

    useEffect(() => {
        dispatch(getAllMoviesThunk())
    }, [dispatch])

    // const openDetails = () => {
    //   if (showDetails) return;
    //   setShowDetails(true);
    // };

    // useEffect(() => {
    //   if (!showDetails) return;

    //   const closeDetails = () => {
    //     setShowDetails(false);
    //   };

    //   const element = document.querySelector('.swiper-indiv-div');
    //   //event listener for drop down menu
    //   element.addEventListener('mouseover', openDetails);
    //   //clean up
    //   // return () => document.removeEventListener("mouseover", closeDetails);
    // }, [showDetails])

    if (!movies) return null;

    return (
        <div className="whole-carousel-outer-div">
        <h1 className="movie-carousel-title">Movies</h1>
          <Swiper
            slidesPerView={6}
            spaceBetween={7}
            slidesPerGroup={6}
            loop={true}
            loopFillGroupWithBlank={true}
            pagination={{
              clickable: true
            }}
            navigation={true}
            modules={[Pagination, Navigation]}
            className="mySwiper"
            allowTouchMove={false}
          >{
            moviesCarousel.map(movie => (
              <SwiperSlide>
              <div className={`swiper-indiv-div ${showDetails && "open-movie-details"}`}>
                <img src={movie.prev_img} alt='movie poster' className="swiper-img"></img>
                <div className="hidden-details-info">
                  <div className="top-half-hidden-details">
                    <div className="play-butt-details">
                      <svg width="13" height="12" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M4 2.69127C4 1.93067 4.81547 1.44851 5.48192 1.81506L22.4069 11.1238C23.0977 11.5037 23.0977 12.4963 22.4069 12.8762L5.48192 22.1849C4.81546 22.5515 4 22.0693 4 21.3087V2.69127Z" fill="currentColor"></path></svg>
                    </div>
                    <div className="add-to-my-list">
                      <svg width="13" height="12" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" class="Hawkins-Icon Hawkins-Icon-Standard"><path fill-rule="evenodd" clip-rule="evenodd" d="M11 2V11H2V13H11V22H13V13H22V11H13V2H11Z" fill="currentColor"></path></svg>
                    </div>

                  </div>
                  <div className="bottom-half-hidden-details">

                  </div>
                </div>
              </div>
            </SwiperSlide>
            ))
          }
          </Swiper>
        </div>
      );
}
