import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllMoviesThunk } from '../../store/movies';
import './homePage.css'

export default function HomePage() {
    const dispatch = useDispatch();
    const movies = Object.values(useSelector(state => state.movies))

    useEffect(() => {
        dispatch(getAllMoviesThunk())
      }, [dispatch])

    return (
        <div className='whole-outer-movie-body'
        style={{
            backgroundSize: "cover",
            backgroundImage: `url('https://s.yimg.com/ny/api/res/1.2/J.Ku1QD1j5L.aSQx7OArNA--/YXBwaWQ9aGlnaGxhbmRlcjt3PTY0MDtoPTM2MA--/https://s.yimg.com/os/en_US/News/BGR_News/star-wars-the-force-awakens-wide-poster.jpg')`,
        }}>
