import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { editProfileThunk, getAllProfilesThunk } from "../../store/profiles";
import './editProfile.css'

export default function EditProfile() {
    const dispatch = useDispatch();
    const history = useHistory();
    const { id } = useParams();
    const profiles = Object.values(useSelector(state => state.profiles))
    const profile = profiles[+id - 1]
    const [username, setUsername] = useState(profile?.username || "")
    const [img, setImg] = useState(profile?.profile_img || "")
    const [validationErrors, setValidationErrors] = useState([]);
    const [hasSubmitted, setHasSubmitted] = useState(false);

    useEffect(() => {
        dispatch(getAllProfilesThunk())
        const errors = [];
        if(!username) errors.push("Username is required");
        if(username.length > 40) errors.push("Username must be less than 40 characters");
        setValidationErrors(errors);
    }, [username, img, dispatch])

    const onSubmit = async (e) => {
        e.preventDefault();

        if (validationErrors.length) return alert(`Cannot Save Changes`);

        const updatedProfile = {
            id: profile.id,
            user_id: profile.user_id,
            username, 
            profile_img:img

        }

        const newProfile = await dispatch(editProfileThunk(updatedProfile))
    }

    // console.log(profile)
    if (!profile) return null;

    return (
        <div className="outer-edit-prof">
            <div className="inner-edit-prof">
                <div className="edit-prof-div">
                    <h1 className="edit-prof-text">Edit Profile</h1>
                </div>
                <div className="outer-prof-details">
                    <div className="prof-img-div">
                    <img src={profile.profile_img} alt='profile image' className="edit-prof-img"></img>
                    </div>
                    <form className="inner-prof-dets">
                        <div>
                            <input className="input-username"
                            type="text"
                            onChange={(e) => setUsername(e.target.value)}
                            value={username}
                            placeholder="Name"/>
                        </div>
                        <span>Language:</span>
                        <button className='language-button'>  
                            <i class="fa-solid fa-globe"></i>
                            English
                        </button>
                    </form>
                </div>
                <div className="edit-prof-controls">
                    <button className="done-manage-button">Save</button>
                    <button onClick={() => history.push('/profiles/manage')} className="manage-prof-button">Cancel</button>
                    <button className="manage-prof-button">Delete Profile</button>
                </div>
            </div>
        </div>
    )
}
