import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";

export default function AddProfile() {
    const dispatch = useDispatch();
    const history = useHistory();
    const [username, setUsername] = useState("")
    const [validationErrors, setValidationErrors] = useState([]);
    const sessionUser = useSelector(state => state.session.user);

    useEffect(() => {
        const errors = [];
        if (!username) errors.push("Username is required");
        if (username.length > 40) errors.push("Username must be less than 40 characters");
        setValidationErrors(errors);
    }, [username])

    // const onSubmit = async () => {

    //     if (validationErrors.length) return alert(`Cannot Add Profile`);

    //     const newProfile = {
    //         user_id: profile.user_id,
    //         username,
    //         profile_img: img

    //     }

    //     await dispatch(editProfileThunk(updatedProfile))
    // }


    return (
        <form>
            <h1>Add Profile</h1>
            <span>Add a profile for another person watching Netflix.</span>
            <div className="outer-add-prof">
                <img src="https://occ-0-1339-1340.1.nflxso.net/dnm/api/v6/K6hjPJd6cR6FpVELC5Pd6ovHRSk/AAAABUoj4FT-0Rr558WbETiintMnmH2JKw4l_p4MdMoxqVx7YXwsvLvvnGUtx3HKZN_BJFH4EHpXn5KqSCBVxLrRz0n4gk64yyeAFw.png?r=229" alt="profile image"></img>
                    <div className="outer-input-div">
                        <input className="input-username"
                            type="text"
                            onChange={(e) => setUsername(e.target.value)}
                            value={username}
                            placeholder="Name" />
                    </div>
            </div>
        </form>
    )
}
