import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import { editProfileThunk, getAllProfilesThunk } from "../../store/profiles";
import './editProfile.css'

export default function EditProfile() {
    const dispatch = useDispatch();
    const { id } = useParams();

    useEffect(() => {
        dispatch(getAllProfilesThunk())
    }, [dispatch])

    const profiles = Object.values(useSelector(state => state.profiles))
    const profile = profiles[+id -1]
    // console.log(profile)
    if(!profile) return null;

    return (
        <div className="outer-edit-prof">
            <h1>Edit Profile</h1>
            <img src={profile.profile_img} alt='profile image'></img>
            <h2>{profile.username}</h2>
            <button className="manage-prof-button">Delete Profile</button>
            <button className="manage-prof-button">Delete Profile</button>
        </div>
    )
}
