import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getAllMoviesThunk } from '../../store/movies';
import './homePage.css'

export default function HomePage() {
    const dispatch = useDispatch();
    const movies = Object.values(useSelector(state => state.movies))
    const headerMovie = useSelector(state => state.movies[21])

    useEffect(() => {
        dispatch(getAllMoviesThunk())
    }, [dispatch])

    if (!headerMovie || !movies) return null;

    return (
        <div className='whole-outer-home-page'>
            <div className='whole-outer-movie-body'
                style={{
                    backgroundSize: "cover",
                    backgroundImage: `url("${headerMovie?.prev_img}")`,
                    backgroundPosition: 'center center'
                }}>
                <div className='banner-buttons'>
                    <Link to={`watch/${headerMovie.id}`} exact={true}>
                        <button className='play-button-header'>Play</button>
                    </Link>
                    <button className='details-button-header'>More Info</button>
                </div>
            </div>
            <div className='fade-bottom'></div>
            {movies.map(mov => (
                <div>
                    <h2>{mov.movie_name}</h2>
                    <img src={mov.prev_img} alt='movie poster' className='movie-poster'></img>
                </div>
            ))}
        </div>
    )
}
